import java.util.Collections;
import java.util.List;
import java.util.Optional;
import java.util.Scanner;

public class TrainBooking {

    public static void main(String[] arg) {
        PrepareTrain prepareTrain = new PrepareTrain();
        int trainNo;
        String selectedCoatchName;

        Scanner trainScanner = new Scanner(System.in);
        Scanner coatchScanner = new Scanner(System.in);
        Scanner seatScanner = new Scanner(System.in);

        final List<Train> trains = prepareTrain.readyTrain();
        Collections.sort(trains, new IdComprator());

        System.out.println("Welcome to Train booking");
        System.out.println("Please select train by select train No.");
        for (Train train : trains) {
            System.out.println("Train No.:- " + train.getTrainNo() + " ::: Train Name:- " + train.getTrainName());
        }
        trainNo = trainScanner.nextInt();
        Optional<Train> option = trains.stream().filter(s -> s.getTrainNo() == trainNo).findFirst();
        if (option.isPresent()) {
            final Train selectedTrain = option.get();
            System.out.println("Your Train is:: " + selectedTrain.getTrainName());
            System.out.println("Please Select Coach:");
            for (Coatch trainCoatch : selectedTrain.getCoatchs()) {
                System.out.print(trainCoatch.getCoatchNo() + " , ");
            }
            System.out.println();
            selectedCoatchName = coatchScanner.nextLine();
            Optional<Coatch> optionalCoatch = selectedTrain.getCoatchs().stream()
                    .filter(s -> s.getCoatchNo().equals(selectedCoatchName)).findFirst();
            if (optionalCoatch.isPresent()) {
                final Coatch selectedCoatch = optionalCoatch.get();
                System.out.println("Your Coatch is :: " + selectedCoatch.getCoatchNo());
                System.out.println("Please Select Seat No:");
                System.out.println();

                for (Seat trainSeat : selectedCoatch.getSeats()) {

                    if (trainSeat.getSeatPosition().equals("UB") || trainSeat.getSeatPosition().equals("SUB")) {
                        System.out.print(trainSeat.getSeatNo() + "=>" + trainSeat.getSeatPosition());

                    } else {
                        System.out.print(trainSeat.getSeatNo() + "=>" + trainSeat.getSeatPosition() + " :: ");

                    }
                    if (trainSeat.getSeatPosition().equals("UB")) {

                        System.out.println();
                    }
                    if (trainSeat.getSeatPosition().equals("SUB")) {
                        System.out.println();
                        System.out.println();
                    }

                }
                int selectedSeatNo = seatScanner.nextInt();

                Optional<Seat> optionalSeat = selectedCoatch.getSeats().stream()
                        .filter(s -> s.getSeatNo() == selectedSeatNo).findFirst();

                if (optionalSeat.isPresent()) {
                    final Seat selectedSeat = optionalSeat.get();

                    if (selectedSeat.getStatus() == 0) {

                        System.out.println("***Conguration Your Seat book successfully The Details is Below***");
                        System.out.println();
                        System.out.println(
                                "Train name & No.  : " + selectedTrain.getTrainName() + " "
                                        + selectedTrain.getTrainNo());
                        System.out.println("The Coatch is  : " + selectedCoatch.getCoatchNo());
                        System.out.println(
                                "The Seat is  : " + selectedSeat.getSeatNo() + " => " + selectedSeat.getSeatPosition());
                    } else {
                        System.out.println("Seat Have Already booked");
                    }

                }

            }

        } else {
            System.out.println("Train is not present ");
        }
        trainScanner.close();
        seatScanner.close();
        coatchScanner.close();

    }

}
